mapper.select:
SELECT Y.* FROM (  
	SELECT ROWNUM AS NUM, X.* FROM (
		SELECT 
			ROWNUM AS RNUM, SSEQ, SUBJECT FROM dkdlsprtus.home_board ORDER BY REGDATE DESC 
	) X	WHERE ROWNUM <= 4
) Y WHERE NUM > 0

mapper.update:
UPDATE dkdlsprtus.home_board SET 
	CNT = #{cnt}
WHERE SSEQ = #{sseq}

UPDATE dkdlsprtus.home_board SET
	SUBJECT = #{subject}
	,CONTENT = #{content}
	,WRITER = #{writer}
WHERE SSEQ = #{sseq}

UPDATE dkdlsprtus.home_board SET
	ORDERNO = ORDERNO+1
WHERE GROUPNO = #{groupNo} AND ORDERNO > #{orderNo}
