mapper.select:




		SELECT Y.* FROM (
			SELECT @NUM:=@NUM+1 AS NUM, X.* FROM (
        		SELECT W.* FROM (
        			SELECT @RNUM:=@RNUM+1 AS RNUM, SSEQ, SUBJECT FROM dkdlsprtus.home_ref, (SELECT @RNUM:=0) V  ORDER BY SSEQ ASC
        		) W ORDER BY W.RNUM DESC
			) X, (SELECT @NUM:=0) U
			WHERE (SELECT @RN:=@RN+1 FROM (SELECT @RN:=0) P) <= 4
		) Y WHERE NUM > 0

SELECT Y.* FROM ( SELECT @NUM:=@NUM+1 AS NUM, X.* FROM ( SELECT W.* FROM ( SELECT @RNUM:=@RNUM+1 AS RNUM ,SSEQ ,SUBJECT ,CONTENT ,REGDATE ,WRITER ,CNT ,ISFILE ,FILENAME ,FILERENAME ,FILE_PATH FROM dkdlsprtus.home_ref, (SELECT @RNUM:=0) V
WHERE ${searchType} LIKE '%${searchString}%'
ORDER BY SSEQ ASC ) W ORDER BY W.RNUM DESC ) X, (SELECT @NUM:=0) U WHERE (SELECT @RN:=@RN+1 FROM (SELECT @RN:=0) P) <= #{endRow} ) Y WHERE NUM > #{startRow}

mapper.update:
UPDATE dkdlsprtus.home_ref SET 
	CNT = #{cnt}
